mapper.select:
SELECT register_time,register_date,COUNT(register_date) AS yy_count FROM( SELECT register_date,register_time FROM clinic_client_register WHERE 1=1 ORDER BY register_time,register_date ) a WHERE 1=1
AND register_date=#{registerDate}
AND register_time=#{registerTime}
GROUP BY register_time,register_date ORDER BY register_date,register_time

SELECT id,user_id,register_date,register_time,rpt_type,
work_info,work_addr,remarks,rpt_flg,stood,is_cancel
FROM clinic_client_register
WHERE 1=1

    AND user_id=#{userId}


    AND register_date=#{registerDate}


    AND register_time=#{registerTime}


    AND is_cancel=#{isCancel}

ORDER BY id

SELECT register_date,register_time,register_used
FROM clinic_mgr_yyconfig
WHERE 1=1

    AND register_used=#{registerUsed}


    AND  register_date > #{registerDate} 

ORDER BY register_date DESC

SELECT id,a.user_id,register_date,register_time,rpt_type,
work_info,work_addr,remarks,rpt_flg,stood,is_cancel
FROM clinic_client_register a
LEFT JOIN clinic_client_user b ON a.user_id = b.user_id
WHERE 1=1

    AND b.user_sms=#{sms}


    AND a.user_id=#{userId}


    AND register_date=#{registerDate}


    AND register_time=#{registerTime}


    AND is_cancel=#{isCancel}

ORDER BY id DESC

mapper.update:
UPDATE clinic_client_register
SET rpt_flg = #{rptFlg}
WHERE 1=1
AND user_id = #{userId}
AND user_sms = #{sms}
AND register_date = #{registerDate}
AND register_time = #{registerTime}
